#else
  return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(std140) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
    if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
    if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 color;
#if USE_TEXTURE
  in vec2 uv0;
  uniform sampler2D cc_spriteTexture;
#endif
layout(std140) uniform Hue {
  float targetHue;
  float hueRange;
  float hueRotationAngle;
};
vec3 rgbToHsv(vec3 rgb) {
  float r = rgb.r;
  float g = rgb.g;
  float b = rgb.b;
  float minVal = min(min(r, g), b);
  float maxVal = max(max(r, g), b);
  float delta = maxVal - minVal;
  float h = 0.0;
  float s = 0.0;
  float v = maxVal;
  if (delta != 0.0) {
      if (maxVal == r) {
          h = (g - b) / delta;
      } else if (maxVal == g) {
          h = 2.0 + (b - r) / delta;
      } else {
          h = 4.0 + (r - g) / delta;
      }
      h *= 60.0;
      if (h < 0.0) {
          h += 360.0;
      }
      s = delta / maxVal;
  }
  return vec3(h, s, v);
}
vec3 hsvToRgb(vec3 hsv) {
    float h = hsv.x;
    float s = hsv.y;
    float v = hsv.z;
    float c = v * s;
    float x = c * (1.0 - abs(mod(h / 60.0, 2.0) - 1.0));
    float m = v - c;
    vec3 rgb;
    if (h >= 0.0 && h < 60.0) {
        rgb = vec3(c, x, 0.0);
    } else if (h >= 60.0 && h < 120.0) {
        rgb = vec3(x, c, 0.0);
    } else if (h >= 120.0 && h < 180.0) {
        rgb = vec3(0.0, c, x);
    } else if (h >= 180.0 && h < 240.0) {
        rgb = vec3(0.0, x, c);
    } else if (h >= 240.0 && h < 300.0) {
        rgb = vec3(x, 0.0, c);
    } else {
        rgb = vec3(c, 0.0, x);
    }
    return rgb + vec3(m);
}
vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o *= CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
    #if IS_GRAY
      float gray  = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
      o.r = o.g = o.b = gray;
    #endif
  #endif
  vec3 hsv = rgbToHsv(vec3(o.r, o.g, o.b));
  float minHue = targetHue - hueRange;
  float maxHue = targetHue + hueRange;
  if (hsv.x >= minHue && hsv.x <= maxHue) {
      hsv.x += hueRotationAngle;
      if (hsv.x > 360.0) {
          hsv.x -= 360.0;
      }
  }
  vec3 newColor = hsvToRgb(hsv);
  o.xyz = newColor;
  o *= color;
  ALPHA_TEST(o);
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

